mapper.select:
		select * 
		  from tb_freeboard f
		 inner join tb_member m on m.member_no = f.member_no
           and f.board_no = #{boardNo}
		  left join tb_busker b on b.member_no = m.member_no;

mapper.insert:
insert into tb_freeboard(
	member_no, content, title, group_no
)values(
	#{memberNo}, #{content}, #{title}, #{groupNo}
)

insert into tb_freeboard(
	member_no, content, title
)values(
	#{memberNo}, #{content}, #{title}
)

mapper.update:
update tb_freeboard
   set title = #{title},
   	   content = #{content},
   	   group_no = #{groupNo}
 where board_no = #{boardNo}

update tb_freeboard
   set title = #{title},
   	   content = #{content}
 where board_no = #{boardNo}

mapper.insert:
insert into tb_freeboard_comment(
	member_no, content, board_no
)values(
	#{memberNo}, #{content}, #{boardNo}
)

insert into tb_like(
	board_no, board_type, member_no
)values(
	#{boardNo}, 1, #{memberNo}
)

mapper.select:
select *
  from tb_like
 where board_no = #{boardNo}
   and member_no = #{memberNo}

mapper.update:
		update tb_like
  		   set like_status = #{likeStatus}
		 where board_no = #{boardNo}
		   and member_no = #{memberNo}

update tb_freeboard
   set like_cnt = like_cnt + 1
 where board_no = #{boardNo}

update tb_freeboard
   set like_cnt = like_cnt - 1
 where board_no = #{boardNo}

mapper.select:
select like_cnt
  from tb_freeboard
 where board_no = #{boardNo}

mapper.insert:
insert into tb_freeboard_comment(
	member_no, content, board_no, reply_no
)values(
	#{memberNo}, #{content}, #{boardNo}, #{replyNo}
)

mapper.update:
		update tb_freeboard_comment
		   set content = '삭제된 댓글 입니다.'
 		 where comment_no = #{commentNo}

mapper.select:
select count(*) 
  from tb_freeboard_comment
 where reply_no = #{commentNo}

mapper.update:
	update tb_freeboard_comment
 	   set content = #{content}
	 where comment_no = #{commentNo}

mapper.insert:
insert into tb_like(
	member_no, board_no, board_type, like_status
) values (
	#{memberNo}, #{boardNo}, 2, 'y'
)

mapper.select:
    	select like_cnt 
		  from tb_freeboard_comment
		 where comment_no = #{commentNo};

select *
  from tb_like
 where board_no = #{boardNo}
   and member_no = #{memberNo}
   and board_type = 2

mapper.update:
   		update tb_freeboard_comment
		   set like_cnt = like_cnt + 1
		 where comment_no = #{commentNo}

    	update tb_freeboard_comment
		   set like_cnt = like_cnt - 1
		 where comment_no = #{commentNo}

mapper.insert:
insert into tb_like(
	member_no, board_no, board_type, dislike_status
) values (
	#{memberNo}, #{boardNo}, 2, 'y'
)

mapper.select:
    	select dislike_cnt 
		  from tb_freeboard_comment
		 where comment_no = #{commentNo};

select *
  from tb_like
 where board_no = #{boardNo}
   and member_no = #{memberNo}
   and board_type = 2

mapper.update:
   		update tb_freeboard_comment
		   set dislike_cnt = dislike_cnt + 1
		 where comment_no = #{commentNo}

    	update tb_freeboard_comment
		   set dislike_cnt = dislike_cnt - 1
		 where comment_no = #{commentNo}

update tb_freeboard
   set is_deleted = 'Y'
 where board_no = #{boardNo}
