mapper.insert:
insert into tb_member (
	id, pass, email, sex, nick_name
) values (
	#{id}, #{pass}, #{email}, #{sex}, #{nickName}
)

mapper.update:
update tb_member
   set nick_name = #{nickName}
 where id = #{id}

mapper.select:
select count(*)
  from tb_member
 where id = #{id}

select count(*)
  from tb_member
 where email = #{email}

select count(*)
  from tb_member
 where nick_name = #{nickName}

select id
  from tb_member
 where email = #{email}

select pass
  from tb_member
 where id = #{id} 
   and email = #{email}

mapper.update:
update tb_member
   set pass = #{pass}
 where id = #{id}
   and email = #{email}

