mapper.update:
update tb_qnaboard
   set view_cnt = view_cnt + 1
 where board_no = #{boardNo}

update tb_qnaboard
   set title = #{title},
       content = #{content}
 where board_no = #{boardNo}

mapper.select:
select *
  from tb_like
 where board_no = #{boardNo}
   and member_no = #{memberNo}
   and board_type=#{boardType}

mapper.update:
update tb_like
   set like_status = #{likeStatus}
 where board_no = #{boardNo}
   and member_no = #{memberNo}
   and board_type = #{boardType}

    	update tb_qnaboard
		   set like_cnt = like_cnt + 1
		 where board_no = #{boardNo}

    	update tb_qnaboard
		   set like_cnt = like_cnt - 1
		 where board_no = #{boardNo}
